import React, { Component } from "react";
import { Sprite, Stage, TilingSprite } from "react-pixi-fiber";
import { render, Text } from "react-pixi-fiber";
import * as PIXI from "pixi.js";

const canvasElement = document.getElementById("container");

const height = 400;
const width = 400;

const bgTexture = PIXI.Texture.fromImage(
  "http://pixijs.io/pixi-filters/tools/demo/images/displacement_BG.jpg"
);
const overlayTexture = PIXI.Texture.fromImage(
  "http://pixijs.io/pixi-filters/tools/demo/images/overlay.png"
);
const displacementTexture = PIXI.Texture.fromImage(
  "http://pixijs.io/pixi-filters/tools/demo/images/displacement_map.png"
);

class ProjectPreview extends Component {
  constructor(props) {
    super(props);

    this.state = {
      filters: []
    };

    this.displacementSprite = React.createRef();
    this.overlaySprite = React.createRef();
  }

  componentDidMount() {
    this.setState({
      filters: [
        new PIXI.filters.DisplacementFilter(this.displacementSprite.current)
      ]
    });

    PIXI.ticker.shared.add(this.move, this);
  }

  componentWillUnmount() {
    PIXI.ticker.shared.remove(this.move, this);
  }

  move() {
    this.overlaySprite.current.tilePosition.x -= 1;
    this.overlaySprite.current.tilePosition.y -= 1;
  }

  render() {
    return (
      <Stage
        options={OPTIONS}
        width={width}
        height={height}
        filters={this.state.filters}
      >
        <Sprite texture={bgTexture} />
        <TilingSprite
          ref={this.overlaySprite}
          texture={overlayTexture}
          width={512}
          height={512}
        />
        <Sprite ref={this.displacementSprite} texture={displacementTexture} />
      </Stage>
    );
  }
}

export default ProjectPreview;
