import React from "react";
import { Container, Row, Col } from "react-bootstrap";
import Projects from "./projects.json";

const PortfolioItem = props => {
  const projectTypeList = props.projectType.map(o => <li>{o.name}</li>);

  return (
    <Container className="portfolio-item">
      <h1>{props.displayName}</h1>
      <img src={props.MainImage} alt={props.displayName} />
      <Row>
        <Col lg={4}>
          <Col lg={6}>{props.date}</Col>
          <Col lg={6}>{projectTypeList}</Col>
        </Col>
        <Col>{props.summary}</Col>
      </Row>
      <p>{Projects.projects[0].url}</p>
    </Container>
  );
};

export default PortfolioItem;
