  const { leftWidth = 10, topHeight = 10, rightWidth = 10, bottomHeight = 10 } = props
  const texture = getTextureFromProps('NineSlicePlane', props)

  const nineSlicePlane = new PixiNineSlicePlane(texture, leftWidth, topHeight, rightWidth, bottomHeight)

  nineSlicePlane.applyProps = (instance, oldProps, newProps) => {
    const { image, texture, ...props } = newProps
    applyDefaultProps(instance, oldProps, props)

    if (image || texture) {
      instance.texture = getTextureFromProps('NineSlicePlane', newProps)
    }
  }

  return nineSlicePlane
}

export default NineSlicePlane
import { ParticleContainer as PixiParticleContainer } from 'pixi.js'

const ParticleContainer = (root, props) => {
  const { maxSize = 1500, batchSize = 16384, autoResize = false } = props

  const properties = {
    ...{
      vertices: false,
      scale: true,
      position: true,
      rotation: true,
      uvs: true,
      tint: true,
    },
    ...(props.properties || {}),
  }

  const destroyOptions = {
    ...{
      children: false,
      texture: false,
      baseTexture: false,
    },
    ...(props.destroyOptions || {}),
  }

  const container = new PixiParticleContainer(maxSize, properties, batchSize, autoResize)

  container.willUnmount = (instance, child, parent) => {
    instance.destroy(destroyOptions)
  }

  return container
}

export default ParticleContainer
import { Sprite as PixiSprite } from 'pixi.js'
import { getTextureFromProps, applyDefaultProps } from '../utils/props'

const Sprite = (root, props) => {
  const sprite = new PixiSprite(getTextureFromProps('Sprite', props))

  sprite.applyProps = (instance, oldProps, newProps) => {
    const { image, texture, ...props } = newProps
    applyDefaultProps(instance, oldProps, props)

    if ((texture && oldProps.texture !== newProps.texture) || (image && oldProps.image !== newProps.image)) {
      instance.texture = getTextureFromProps('Sprite', newProps)
    }
  }

  return sprite
}

export default Sprite
import { AnimatedSprite as PixiAnimatedSprite } from 'pixi.js'
import { getTextureFromProps, applyDefaultProps } from '../utils/props'

const AnimatedSprite = (root, props) => {
  const { textures, images, autoUpdate, isPlaying = true, initialFrame } = props
  const makeTexture = textures => {
    return textures.map(texture => {
      return getTextureFromProps('AnimatedSprite', {
        texture,
      })
    })
  }
  const animatedSprite = images ? PixiAnimatedSprite.fromImages(images) : new PixiAnimatedSprite(makeTexture(textures))
  animatedSprite[isPlaying ? 'gotoAndPlay' : 'gotoAndStop'](initialFrame || 0)
