    const { textures, ...props } = newProps
    const { isPlaying, initialFrame } = props
    applyDefaultProps(instance, oldProps, props)
    if (textures) {
      instance.textures = makeTexture(textures)
      animatedSprite[isPlaying ? 'gotoAndPlay' : 'gotoAndStop'](initialFrame || 0)
    }
  }

  return animatedSprite
}

export default AnimatedSprite
import { Text as PixiText, Sprite as PixiSprite } from 'pixi.js'

const Text = (root, props) => {
  const { text = '', style = {}, isSprite } = props
  const pixiText = new PixiText(text, style)
  if (isSprite) {
    pixiText.updateText()
    return new PixiSprite(pixiText.texture)
  }
  return pixiText
}

export default Text
