import React from "react";
import { Route, Switch } from "react-router-dom";
import LandingPage from "../../components/LandingPage";
import PortfolioItem from "../../components/PortfolioItem";
import Projects from "../../components/projects.json";

const Routes = () => {
 // const imagesGallery = props.images.map(o => <img src={o} alt="" />);

  return (
    <Route
      render={({ location }) => (
        <Switch location={location}>
          <Route exact path="/" component={LandingPage} key="landingpage" />
          <Route
            path={Projects.projects[0].url}
            render={props => (
              <PortfolioItem
                {...props}
                displayName={Projects.projects[0].displayName}
                date={Projects.projects[0].date}
                summary={Projects.projects[0].summary}
                projectType={Projects.projects[0].projectType}
                mainImage={Projects.projects[0].mainImage}
                images={Projects.projects[0].images}
              />
            )}
          />
        </Switch>
      )}
    />
  );
};

export default Routes;
