import React from "react";
import { Container, Row, Col } from "react-bootstrap";

const PortfolioItem = props => {
  const projectTypeList = props.projectType.map((projectTypes, i) => (
    <li key={i}>{projectTypes}</li>
  ));

  const imagesGallery = props.images.map((imageList, i) => (
    <img key={i} src={`../img/${props.url}/${imageList}`} alt="" />
  ));

  return (
    <Container className="portfolio-item">
      <h1>{props.displayName}</h1>
      <img src={`../img/${props.url}/${props.MainImage}`} alt={props.displayName} />
      <Row>
        <Col lg={4}>
          <Col lg={6}>{props.date}</Col>
          <Col lg={6}>
            <ul>{projectTypeList}</ul>
          </Col>
        </Col>
        <Col>{props.summary}</Col>
      </Row>
      {imagesGallery}
    </Container>
  );
};

export default PortfolioItem;
