import React from "react";
import { Row, Col } from "react-bootstrap";
import Header from "./Header";
import Projects from "./projects.json";
import { Link } from "react-router-dom";
import LaPrinciere from "../img/la-princiere/LaPrinciere1.jpg";
import Calleis from "../img/calleis/Calleis-TheBody.jpg";
class LandingPage extends React.Component {
  constructor(props, prevState) {
    super(props);
    this.state = {
      activeSlide: 0,
      previousSlide: "",
      isActive: true
    };
  }

  // getNewSlide = () => {
  //   let activeSlide = Math.floor(Math.random() * 15);
  //   this.setState(
  //     prevState => {
  //       return {
  //         activeSlide: activeSlide,
  //         previousSlide: prevState.activeSlide
  //       };
  //     },
  //     () => console.log(this.state)
  //   );
  // };

  // getPreviousSlide = () => {
  //   this.setState(
  //     {
  //       activeSlide: this.state.previousSlide,
  //       previousSlide: ""
  //     },
  //     () => console.log(this.state)
  //   );
  // };

  render() {
    return (
      <>
        <Header />
        <Row className="content homepage">
          <Col lg={6}>
            <div className="coord">
              <a
                href="https://goo.gl/maps/6gsWDXBXcj5sqtkY9"
                target="_blank"
                rel="noopener noreferrer"
              >
                <h3 className="coord">55.966885° N, -3.188179° E</h3>
              </a>
            </div>
            <div className="main-menu">
              {Projects.projects.map((projectList, i) => (
                <Row
                  className="menu-item"
                  key={i}
                  onMouseOver={() =>
                    // this.setState({ activeSlide: i, isActive: true })
                    this.setState(
                      prevState => {
                        return {
                          activeSlide: i,
                          previousSlide: prevState.i
                        };
                      },
                      () => console.log(this.state)
                    )
                  }
                >
                  <Col lg={1}>
                    <h6>{`0${i + 1}`}</h6>
                  </Col>
                  <Col>
                    <Link to={projectList.url}>
                      <h1>{projectList.displayName}</h1>
                    </Link>
                  </Col>
                </Row>
              ))}
            </div>
          </Col>
          <Col className="preview" lg={6}>
            <div
              className={`preview-image`}
              style={{
                backgroundImage: `url(${require(`../img${Projects.projects[this.state.activeSlide].url}/${Projects.projects[this.state.activeSlide].mainImage}`)})`
              }}
            />
          </Col>
        </Row>
      </>
    );
  }
}

export default LandingPage;
