import React from "react";
import videojs from "video.js";
import "video.js/dist/video-js.css";
// import videoPoster from "../images/Video_Thumb.jpg";

class VideoPlayer extends React.Component {
  componentDidMount() {
    this.player = videojs(this.videoNode, this.props, function onPlayerReady() {});
    this.setupPlayerEvents();
  }

  componentWillUnmount() {
    if (this.player) {
      this.player.dispose();
    }
  }

  setupPlayerEvents() {
    this.player.on("play", () => {
      const category = "Video";
      const action = "Click";
      const label = "Play";
      this.props.trackEvent(category, action, label);
    });
  }

  render() {
    // const playcard = this.props.playcard || videoPoster;
    return (
      <div>
          <p>{this.props.video}</p>
        {/* <div data-vjs-player>
          <video ref={node => (this.videoNode = node)} className='video-js vjs-16-9 vjs-big-play-centered' poster={playcard} />
        </div> */}
      </div>
    );
  }
}

export default VideoPlayer;
