import React from "react";
import { Shaders, Node, GLSL } from "gl-react";
import { Surface } from "gl-react-dom";
import timeLoop from "./TimeLoop";

const shaders = Shaders.create({
  vignetteColorSeparationDistortion: {
    frag: GLSL`
    uniform float time;
    uniform float progress;
    uniform float width;
    uniform float scaleX;
    uniform float scaleY;
    uniform float transition;
    uniform float radius;
    uniform float intensity;
    varying vec2 vUv;

    void main()	{
      vec2 newUV = (vUv - vec2(0.5))*resolution.zw + vec2(0.5);

     vec4 d1 = texture2D(texture1, newUV);
     vec4 d2 = texture2D(texture2, newUV);

     float displace1 = (d1.r + d1.g + d1.b)*0.33;
     float displace2 = (d2.r + d2.g + d2.b)*0.33;

     vec4 t1 = texture2D(texture1, vec2(newUV.x, newUV.y + progress * (displace2 * intensity)));
     vec4 t2 = texture2D(texture2, vec2(newUV.x, newUV.y + (1.0 - progress) * (displace1 * intensity)));

     gl_FragColor = mix(t1, t2, progress);

    }
  `
  }
});

const Vignette = timeLoop(({ children: t, time, mouse }) => (
  <Node
    shader={shaders.vignetteColorSeparationDistortion}
    uniforms={{
      t,
      time: time / 1000, // seconds is better for float precision
      mouse,
      freq: 10 + 2 * Math.sin(0.0007 * time),
      amp: 0.05 + Math.max(0, 0.03 * Math.cos(0.001 * time)),
      moving: 0,
      progress: { type: "f", value: 0 },
      border: { type: "f", value: 0 },
      intensity: { type: "f", value: 0 },
      scaleX: { type: "f", value: 40 },
      scaleY: { type: "f", value: 40 },
      transition: { type: "f", value: 40 },
      swipe: { type: "f", value: 0 },
      width: { type: "f", value: 0 },
      radius: { type: "f", value: 0 }
    }}
  />
));

class Slide extends React.Component {
  state = {
    mouse: [0.5, 0.5]
  };
  render() {
    const { mouse } = this.state;
    return (
      <Surface width={500} height={400} onMouseMove={this.onMouseMove}>
        <Vignette mouse={mouse}>https://i.imgur.com/2VP5osy.jpg</Vignette>
      </Surface>
    );
  }
  onMouseMove = e => {
    const rect = e.target.getBoundingClientRect();
    this.setState({
      mouse: [
        (e.clientX - rect.left) / rect.width,
        (rect.bottom - e.clientY) / rect.height
      ]
    });
  };
}

export default Slide;
