import React from "react";
import { Row, Col } from "react-bootstrap";
import Header from "./Header";
import Projects from "./projects.json";
import { Link } from "react-router-dom";
class LandingPage extends React.Component {
  constructor(props, prevState) {
    super(props);
    this.state = {
      activeSlide: 0,
      previousSlide: "",
      transitionClass: ""
    };
  }

  setTransitionClass() {
    if (this.state.activeSlide !== this.state.previousSlide) {
      this.setState({ transitionClass: "isFadingOut" });
      setTimeout(() => {
        this.setState({
          transitionClass: "isFadingIn"
        });
      }, 400);
      console.log("the slides have changed");
    } else {
      this.setState({ transitionClass: "isFadingIn" });
      console.log("the slides have not changed");
    }
  }

  render() {
    return (
      <>
        <Header />
        <Row className="content homepage">
          <Col lg={6}>
            <div className="coord">
              <a
                href="https://goo.gl/maps/6gsWDXBXcj5sqtkY9"
                target="_blank"
                rel="noopener noreferrer"
              >
                <h3 className="coord">55.966885° N, -3.188179° E</h3>
              </a>
            </div>
            <div className="main-menu">
              {Projects.projects.map((projectList, i) => (
                <Row
                  className="menu-item"
                  key={i}
                  onMouseEnter={() =>
                    this.setState(
                      prevState => {
                        return {
                          activeSlide: i,
                          previousSlide: prevState.activeSlide
                        };
                      },
                      () => console.log(this.state)
                    )
                  }
                  // onMouseOut={this.setTransitionClass.bind(this)}
                >
                  <Col lg={1}>
                    <h6>{`0${i + 1}`}</h6>
                  </Col>
                  <Col>
                    <Link to={projectList.url}>
                      <h1>{projectList.displayName}</h1>
                    </Link>
                  </Col>
                </Row>
              ))}
            </div>
          </Col>
          <Col className="preview" lg={6}>
            <div
              className={`preview-image ${this.state.transitionClass}`}
              // style={{
              //   backgroundImage: `url(${require(`../img${Projects.projects[this.state.activeSlide].url}.jpg`)})`
              // }}
            >
              <img
                src={require(`../img${Projects.projects[this.state.activeSlide].url}.jpg`)}
                alt={Projects.projects[this.state.activeSlide].displayName}
              />
            </div>
          </Col>
        </Row>
      </>
    );
  }
}

export default LandingPage;
