import React from "react";
import { slide as Menu } from "react-burger-menu";

class Nav extends React.Component {
  showSettings(event) {
    event.preventDefault();
  }

  constructor(props) {
    super(props);
    this.state = {
      menuOpen: false,
      animatedClassName: ""
    };
  }

  handleStateChange(state) {
    this.setState({ menuOpen: state.isOpen });
  }

  closeMenu() {
    this.setState({ menuOpen: false, animatedClassName: "" });
  }

  toggleMenu() {
    this.setState(state => ({ menuOpen: !state.menuOpen }));
    if (this.state.menuOpen === false) {
      this.setState({ animatedClassName: "animated-in" });
      setTimeout(() => {
        this.setState({ animatedClassName: "animated" });
      }, 500);
    } else {
      this.setState({ animatedClassName: "" });
    }
  }

  render() {
    return (
      <>
        <div
          className={`button_container ${
            this.state.menuOpen ? "close_menu" : "open_menu"
          }`}
          onClick={() => this.toggleMenu()}
        >
          <span></span>
          <span></span>
          <span></span>
        </div>
        <Menu
          right
          disableAutoFocus
          width={"100%"}
          noOverlay
          customBurgerIcon={false}
          customCrossIcon={false}
          isOpen={this.state.menuOpen}
          onStateChange={state => this.handleStateChange(state)}
        >
          <div className="bm-menu-content">
            <nav className="bm-menu-list">
              <ul>
                <li className={this.state.animatedClassName}>
                  <a
                    href="https://www.behance.net/antonettiserena"
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    <h1>Behance</h1>
                  </a>
                </li>
                <li className={this.state.animatedClassName}>
                  <a
                    href="https://www.linkedin.com/in/serena-antonetti"
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    <h1>Linkedin</h1>
                  </a>
                </li>
                <li className={this.state.animatedClassName}>
                  <a
                    href="https://codepen.io/antonettis/"
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    <h1>Github</h1>
                  </a>
                </li>
              </ul>
            </nav>
            <p className={this.state.animatedClassName}>
              <a href="mailto:serena.antonetti@gmail.com?subject=Hi!">
                serena.antonetti@gmail.com
              </a>
            </p>
          </div>
        </Menu>
      </>
    );
  }
}

export default Nav;
