import React, { Component } from "react";
import * as THREE from "three";
import { TimelineMax, Power2 } from "gsap/all";
import * as dat from "dat.gui";

class Sketch extends Component {
  constructor(props) {
    super(props);

    this.start = this.start.bind(this);
    this.stop = this.stop.bind(this);
    this.animate = this.animate.bind(this);
  }

  componentDidMount() {
    this.scene = new THREE.Scene();
    this.vertex = `varying vec2 vUv;void main() {vUv = uv;gl_Position = projectionMatrix * modelViewMatrix * vec4( position, 1.0 );}`;
    this.fragment = `
		uniform float time;
		uniform float progress;
		uniform float intensity;
		uniform float width;
		uniform float scaleX;
		uniform float scaleY;
		uniform float transition;
		uniform float radius;
		uniform float swipe;
		uniform sampler2D texture1;
		uniform sampler2D texture2;
		uniform sampler2D displacement;
		uniform vec4 resolution;
		varying vec2 vUv;
		mat2 rotate(float a) {
			float s = sin(a);
			float c = cos(a);
			return mat2(c, -s, s, c);
		}
		const float PI = 3.1415;
		const float angle1 = PI *0.25;
		const float angle2 = -PI *0.75;


		void main()	{
			vec2 newUV = (vUv - vec2(0.5))*resolution.zw + vec2(0.5);

			vec2 uvDivided = fract(newUV*vec2(intensity,1.));


			vec2 uvDisplaced1 = newUV + rotate(3.1415926/4.)*uvDivided*progress*0.1;
			vec2 uvDisplaced2 = newUV + rotate(3.1415926/4.)*uvDivided*(1. - progress)*0.1;

			vec4 t1 = texture2D(texture1,uvDisplaced1);
			vec4 t2 = texture2D(texture2,uvDisplaced2);

			gl_FragColor = mix(t1, t2, progress);

		}
	`;
    this.uniforms = {
      intensity: { value: 50, type: "f", min: 1, max: 100 }
    };
    this.renderer = new THREE.WebGLRenderer();
    this.width = window.innerWidth;
    this.height = window.innerHeight;
    this.renderer.setPixelRatio(window.devicePixelRatio);
    this.renderer.setSize(this.width, this.height);
    this.renderer.setClearColor(0xeeeeee, 1);
    this.duration = 1;
    this.debug = true;
    this.easing = opts.easing || "easeInOut";

    this.clicker = document.getElementById("content");

    this.container = document.getElementById("slider");
    this.images = JSON.parse(this.container.getAttribute("data-images"));
    this.width = this.container.offsetWidth;
    this.height = this.container.offsetHeight;
    this.container.appendChild(this.renderer.domElement);

    this.camera = new THREE.PerspectiveCamera(
      70,
      window.innerWidth / window.innerHeight,
      0.001,
      1000
    );

    this.camera.position.set(0, 0, 2);
    this.time = 0;
    this.current = 0;
    this.textures = [];

    this.paused = true;
    this.start();
  }

  componentWillUnmount() {
    this.stop();
    this.mount.removeChild(this.renderer.domElement);
  }

  start() {
    if (!this.frameId) {
      this.frameId = requestAnimationFrame(this.animate);
    }
  }

  stop() {
    cancelAnimationFrame(this.frameId);
  }

  animate() {
    this.cube.rotation.x += 0.01;
    this.cube.rotation.y += 0.01;

    this.renderScene();
    this.frameId = window.requestAnimationFrame(this.animate);
  }

  renderScene() {
    this.renderer.render(this.scene, this.camera);
  }

  render() {
    return (
      <div
        id="content"
        className="content-demo"
        ref={mount => {
          this.mount = mount;
        }}
      >
        <div
          id="slider"
          data-images='[
        "https://codropspz-tympanus.netdna-ssl.com/codrops/wp-content/uploads/2019/11/Webglimagetransitions_featured.jpg",
        "https://codropspz-tympanus.netdna-ssl.com/codrops/wp-content/uploads/2019/10/g.png",
        "https://codropspz-tympanus.netdna-ssl.com/codrops/wp-content/uploads/2019/10/uv.jpg",
        "LaPrinciere4.jpg",
        "LaPrinciere-Web1.jpg",
        "LaPrinciere-Web5.jpg",
        "LaPrinciere-Web4.jpg",
        "LaPrinciere-Web3.jpg"
    ]'
        ></div>
      </div>
    );
  }
}

export default Sketch;
