import React, { Component } from "react";
import ReactPlayer from "react-player";

export default class VideoPlayer extends React.Component {
  render() {
    const videoUrl = this.props.videoUrl;
    const folderSrc = this.props.folderSrc;

    return (
      <ReactPlayer
        url={[
          { src: `../img${folderSrc}/${videoUrl}.webm`, type: "video/webm" },
          { src: `../img${folderSrc}/${videoUrl}.ogv`, type: "video/ogv" },
          { src: `../img${folderSrc}/${videoUrl}.mp4`, type: "video/mp4" }
        ]}
        controls={false}
        loop={true}
        playing={true}
        muted={true}
      />
    );
  }
}
