import React, { Component } from "react";
import * as THREE from "three";
import { TimelineMax, Power2 } from "gsap/all";
import * as dat from "dat.gui";

class Sketch extends Component {
  constructor(props) {
    super(props);

    this.start = this.start.bind(this);
    this.stop = this.stop.bind(this);
    this.animate = this.animate.bind(this);
  }

  componentDidMount() {
    const width = this.mount.clientWidth;
    const height = this.mount.clientHeight;

    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(75, width / height, 0.1, 1000);
    const renderer = new THREE.WebGLRenderer({ antialias: true });
    const geometry = new THREE.BoxGeometry(1, 1, 1);
    const material = new THREE.MeshBasicMaterial({ color: "#433F81" });
    const cube = new THREE.Mesh(geometry, material);

    camera.position.z = 4;
    scene.add(cube);
    renderer.setClearColor("#000000");
    renderer.setSize(width, height);

    this.scene = scene;
    this.camera = camera;
    this.renderer = renderer;
    this.material = material;
    this.cube = cube;

    this.mount.appendChild(this.renderer.domElement);
    this.start();
  }

  componentWillUnmount() {
    this.stop();
    this.mount.removeChild(this.renderer.domElement);
  }

  start() {
    if (!this.frameId) {
      this.frameId = requestAnimationFrame(this.animate);
    }
  }

  stop() {
    cancelAnimationFrame(this.frameId);
  }

  animate() {
    this.cube.rotation.x += 0.01;
    this.cube.rotation.y += 0.01;

    this.renderScene();
    this.frameId = window.requestAnimationFrame(this.animate);
  }

  renderScene() {
    this.renderer.render(this.scene, this.camera);
  }

  render() {
    return (
      <div id="content" className="content-demo" ref={(mount) => { this.mount = mount }}>
        <div
          id="slider"
          data-images='[
        "https://codropspz-tympanus.netdna-ssl.com/codrops/wp-content/uploads/2019/11/Webglimagetransitions_featured.jpg",
        "https://codropspz-tympanus.netdna-ssl.com/codrops/wp-content/uploads/2019/10/g.png",
        "https://codropspz-tympanus.netdna-ssl.com/codrops/wp-content/uploads/2019/10/uv.jpg",
        "LaPrinciere4.jpg",
        "LaPrinciere-Web1.jpg",
        "LaPrinciere-Web5.jpg",
        "LaPrinciere-Web4.jpg",
        "LaPrinciere-Web3.jpg"
    ]'
        ></div>
      </div>
    );
  }
}

export default Sketch;
